import SlideBar from "../components/slidebar";
import { Component } from "react";
import axios from "axios"
import TableUser from "../components/tableuser";
import Cookies from 'universal-cookie';
import { Link } from "react-router-dom";
import { ToastsContainer, ToastsStore } from 'react-toasts';
import jwt_decode from "jwt-decode";


class Alltask extends Component {
    constructor(props) {
        super(props)
        this.state = {
            resp: [],
            errormsg: ""
        }
    }

    GetAlldata = () => {
        console.log("hiiii")

        let config = {
            headers: {
                tokenkey: localStorage.getItem("token")
            }
        }
        axios.get(`${this.props.ip}/admin/alltask`, config).then((res) => {
            // this.setState({ resp: res.data })
            console.log(res.data)
            if (res.data.error) {
                this.setState({
                    errormsg: res.data.error,
                })
            }
        })
    }

    componentDidMount()
    {
        this.GetAlldata()
    }

    sendUpdateyes = (index) => {
        let config = {
            headers: {
                tokenkey: localStorage.getItem("token")
            }
        }
        axios.post(`${this.props.ip}/user`, ({
            id: this.state.resp[index]._id,
            completed: "yes"
        }), config).then((res) => {
            if (res.data.error) {
                ToastsStore.error(res.data.error)
            }
            else {
                ToastsStore.success(res.data.success)
                this.GetAlldata()
            }

        })
    }

    sendUpdateno = (index) => {
        let config = {
            headers: {
                tokenkey: localStorage.getItem("token")
            }
        }
        axios.post(`${this.props.ip}/user`, ({
            id: this.state.resp[index]._id,
            completed: "no"
        }), config).then((res) => {
            if (res.data.error) {
                ToastsStore.error(res.data.error)
            }
            else {
                ToastsStore.success(res.data.success)
                this.GetAlldata()
            }

        })
    }


    showall = () => this.state.resp.map((val, index) =>

        <TableUser
            sno={index + 1}
            taskname={val.taskname}
            fromTask={val.fromTask}
            toTask={val.toTask}
            completed={val.completed}
            yes={() => { this.sendUpdateyes(index) }}
            no={() => { this.sendUpdateno(index) }}
            assignedBy={val.fromAssign.username}
        />
    )

    render() {
        return (
            <div >
                <div className="row">

                    <SlideBar />

                    <div className="col-sm ">
                        <div style={{ margin: "5% 8%", }}>
                            <div className="table bg-white table-responsive-md" style={{ borderRadius: "10px" }}>
                                <table className="table table-hover">
                                    <thead className="thead-light text-center">
                                        <th>S.NO</th>
                                        <th>Taskname</th>
                                        <th>Start Date</th>
                                        <th>End Date</th>
                                        <th>Completed</th>
                                        <th>Assigned By</th>
                                        <th>Submit</th>
                                    </thead>

                                    <tbody>
                                        {this.showall()}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Alltask
